mapper.select:
SELECT course_id
FROM quiz
WHERE quiz_id = #{quizId}

SELECT COUNT(*)
FROM user_answer ua
         JOIN question q ON ua.question_id = q.question_id
WHERE ua.user_id = #{userId}
  AND q.quiz_id = #{quizId}

mapper.update:
UPDATE quiz
SET title = #{title},
    section_title = #{sectionTitle},
    type = #{type}
WHERE quiz_id = #{quizId}
